const initialState = [];
import {
    SET_ALEERT,
    REMOVE_ALERT
} from '../actions/types';
export default function (state = initialState, action) {
    const {
        type,
        payload
    } = action;
    switch (type) {
        case SET_ALERT:
            return [...state, action.payload]
        case REMOVE_ALERT:
            return state.filter(alert => alert.id !== action.payload)
        default:
            return state;
    }
}
